import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminPage.css';

const AdminPage = () => {
  const [users, setUsers] = useState([]);
  const [newTask, setNewTask] = useState({ task_name: '', user_id: '', task_status: 1 });
  const [loading, setLoading] = useState(true);

  // Fetch users and their tasks
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/users');
        setUsers(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);

  // Handle new task input
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask((prevTask) => ({ ...prevTask, [name]: value }));
  };

  // Submit new task for the user
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/tasks', newTask);
      // Refetch users after adding task
      const response = await axios.get('http://localhost:5000/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error creating task:', error);
    }
  };

  // Update task status (admin functionality)
  const updateTaskStatus = async (taskId, newStatus) => {
    try {
      await axios.put(`http://localhost:5000/tasks/${taskId}`, { task_status: newStatus });
      // Refetch users after status update
      const response = await axios.get('http://localhost:5000/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="admin-page">
      <h1>Admin Dashboard</h1>
      <div className="task-form">
        <h2>Insert New Task</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Task Name:
            <input
              type="text"
              name="task_name"
              value={newTask.task_name}
              onChange={handleInputChange}
              required
            />
          </label>
          <label>
            Assign to User:
            <select
              name="user_id"
              value={newTask.user_id}
              onChange={handleInputChange}
              required
            >
              <option value="">Select a User</option>
              {users.map((user) => (
                <option key={user.id} value={user.id}>
                  {user.username}
                </option>
              ))}
            </select>
          </label>
          <button type="submit">Add Task</button>
        </form>
      </div>

      <div className="users-tasks">
        <h2>Users and Tasks</h2>
        {users.map((user) => (
          <div key={user.id} className="user-section">
            <h3>{user.username}</h3>
            <div className="tasks">
              {user.tasks.map((task) => (
                <div key={task.id} className={`task ${getStatusClass(task.task_status)}`}>
                  <h4>{task.task_name}</h4>
                  <p>Status: {getStatusText(task.task_status)}</p>
                  {/* Allow admin to change from "Check" to "Finished" or back to "In Progress" */}
                  {task.task_status === 3 && (
                    <>
                      <button onClick={() => updateTaskStatus(task.id, 4)}>
                        Mark as Finished
                      </button>
                      <button onClick={() => updateTaskStatus(task.id, 2)}>
                        Move to In Progress
                      </button>
                    </>
                  )}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Helper functions to handle task statuses
const getStatusText = (status) => {
  switch (status) {
    case 1:
      return 'To Do';
    case 2:
      return 'In Progress';
    case 3:
      return 'Check';
    case 4:
      return 'Finished';
    default:
      return 'Unknown';
  }
};

const getStatusClass = (status) => {
  switch (status) {
    case 1:
      return 'todo';
    case 2:
      return 'inprogress';
    case 3:
      return 'check';
    case 4:
      return 'finished';
    default:
      return '';
  }
};

export default AdminPage;
