import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import CryptoJS from 'crypto-js';
import './LoginPage.css';

const SECRET_KEY = 'your-secret-key'; // Use the same secret key as in App.js

function LoginPage({ setIsAuthenticated }) {
  const [id, setId] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Function to encrypt the ID
  const encryptValue = (value) => {
    return CryptoJS.AES.encrypt(value, SECRET_KEY).toString();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/login', { id });

      if (response.data.success) {
        setIsAuthenticated(true); // Set user as authenticated
        localStorage.setItem('userId', encryptValue(id)); // Save encrypted ID to localStorage
        navigate('/dashboard'); // Redirect to dashboard
      } else {
        setError(response.data.message);
      }
    } catch (error) {
      setError('An error occurred during login');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="userId">Enter Your ID</label>
        <input
          type="text"
          id="userId"
          value={id}
          onChange={(e) => setId(e.target.value)}
          required
        />
        <button type="submit">Submit</button>
      </form>
      {error && <p className="error-message">{error}</p>}
    </div>
  );
}

export default LoginPage;
