        const tasksResponse = await axios.get('http://localhost:3001/tasks'); // Modify URL to match your API
        const taskData = tasksResponse.data.results;

        // Organize tasks based on their task_status
        const organizedTasks = {
          todo: [],
          inProgress: [],
          check: [],
