          }
        });

        // Set the tasks
        setTasks(organizedTasks);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);
      console.log(newTask.assignedUser);

      // Refresh task list after creating the new task
      setTasks((prevTasks) => ({
        ...prevTasks,
        todo: [...prevTasks.todo, response.data],
      }));

      setNewTask({ task_name: '', task_status: 1, assignedUser: '' }); // Clear the form
    } catch (error) {
      console.error('Error creating task:', error);
    }
  };

  return (
    <div className="admin-page">
      <h1>Admin Task Management</h1>

      {/* Task creation form */}
      <form onSubmit={handleNewTaskSubmit} className="new-task-form">
        <input
          type="text"
          placeholder="Task Name"
          value={newTask.task_name}
          onChange={(e) => setNewTask({ ...newTask, task_name: e.target.value })}
          required
        />
        <select
          value={newTask.assignedUser}
          onChange={(e) => setNewTask({ ...newTask, assignedUser: e.target.value })}
          required
        >
          <option value="">Assign to User</option>
          {users.map((user) => (
            <option key={user.id} value={user.id}>
              {user.username}
            </option>
          ))}
        </select>
        <button type="submit">Create Task</button>
      </form>

      <DragDropContext onDragEnd={handleOnDragEnd}>
        <div className="columns">
          {/* To Do Column */}
          <Droppable droppableId="todo">
            {(provided) => (
              <div className="column" ref={provided.innerRef} {...provided.droppableProps}>
                <h2>To Do</h2>
                {tasks.todo.map((task, index) => (
                  <Draggable key={task.task_id} draggableId={String(task.task_id)} index={index}>
                    {(provided) => (
                      <div
                        className="card"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <div className="card-body">
                          <h5 className="card-title">{task.task_name}</h5>
                          <p className="card-username">Assigned to: {task.username}</p>
                        </div>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>

          {/* In Progress Column */}
          <Droppable droppableId="inProgress">
            {(provided) => (
              <div className="column" ref={provided.innerRef} {...provided.droppableProps}>
                <h2>In Progress</h2>
                {tasks.inProgress.map((task, index) => (
                  <Draggable key={task.task_id} draggableId={String(task.task_id)} index={index}>
                    {(provided) => (
                      <div
                        className="card"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <div className="card-body">
                          <h5 className="card-title">{task.task_name}</h5>
                          <p className="card-username">Assigned to: {task.username}</p>
                        </div>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>

          {/* Check Column */}
          <Droppable droppableId="check">
            {(provided) => (
              <div className="column" ref={provided.innerRef} {...provided.droppableProps}>
                <h2>Check</h2>
                {tasks.check.map((task, index) => (
                  <Draggable key={task.task_id} draggableId={String(task.task_id)} index={index}>
                    {(provided) => (
                      <div
                        className="card"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <div className="card-body">
                          <h5 className="card-title">{task.task_name}</h5>
                          <p className="card-username">Assigned to: {task.username}</p>
                        </div>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>

          {/* Finished Column */}
          <Droppable droppableId="finished">
            {(provided) => (
              <div className="column" ref={provided.innerRef} {...provided.droppableProps}>
                <h2>Finished</h2>
                {tasks.finished.map((task, index) => (
                  <Draggable key={task.task_id} draggableId={String(task.task_id)} index={index}>
                    {(provided) => (
                      <div
                        className="card"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <div className="card-body">
                          <h5 className="card-title">{task.task_name}</h5>
                          <p className="card-username">Assigned to: {task.username}</p>
                        </div>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </div>
      </DragDropContext>
    </div>
  );
};

export default AdminPage;
